import React, { useRef, useEffect, useState } from 'react';
import './LiveCamera.css';

const LiveCamera = () => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [error, setError] = useState(null);
  const [poseData, setPoseData] = useState(null);

  // Pose connections for drawing skeleton
  const POSE_CONNECTIONS = [
    [11, 12], [11, 13], [13, 15], [12, 14], [14, 16], // Arms
    [11, 23], [12, 24], [23, 24], // Torso
    [23, 25], [25, 27], [24, 26], [26, 28], // Legs
    [15, 17], [15, 19], [15, 21], [16, 18], [16, 20], [16, 22], // Hands
    [27, 29], [27, 31], [28, 30], [28, 32], // Feet
  ];

  // Simple pose detection using MediaPipe-like approach
  const detectPose = async (videoElement) => {
    if (!videoElement || !canvasRef.current) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // For now, we'll simulate pose detection
    // In a real implementation, you'd use MediaPipe or similar
    // This is a placeholder that shows the concept

    // Draw some example pose points to show the overlay is working
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;

    // Draw example skeleton
    ctx.strokeStyle = '#00ff00';
    ctx.lineWidth = 3;
    ctx.lineCap = 'round';

    // Draw torso
    ctx.beginPath();
    ctx.moveTo(centerX, centerY - 50);
    ctx.lineTo(centerX, centerY + 50);
    ctx.stroke();

    // Draw arms
    ctx.beginPath();
    ctx.moveTo(centerX - 30, centerY - 30);
    ctx.lineTo(centerX, centerY - 20);
    ctx.lineTo(centerX + 30, centerY - 30);
    ctx.stroke();

    // Draw legs
    ctx.beginPath();
    ctx.moveTo(centerX - 20, centerY + 50);
    ctx.lineTo(centerX, centerY + 50);
    ctx.lineTo(centerX + 20, centerY + 50);
    ctx.stroke();

    // Draw joints
    ctx.fillStyle = '#ff0000';
    const joints = [
      [centerX, centerY - 50], // Head
      [centerX - 30, centerY - 30], // Left shoulder
      [centerX + 30, centerY - 30], // Right shoulder
      [centerX, centerY - 20], // Torso center
      [centerX - 20, centerY + 50], // Left hip
      [centerX + 20, centerY + 50], // Right hip
    ];

    joints.forEach(([x, y]) => {
      ctx.beginPath();
      ctx.arc(x, y, 4, 0, 2 * Math.PI);
      ctx.fill();
    });
  };

  const startCamera = async () => {
    try {
      setError(null);

      // Request camera access
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user' // Front camera
        }
      });

      const video = videoRef.current;
      if (video) {
        video.srcObject = stream;
        video.play();

        video.addEventListener('loadedmetadata', () => {
          const canvas = canvasRef.current;
          if (canvas) {
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;
          }
          setIsStreaming(true);
        });
      }
    } catch (err) {
      setError(`Camera access denied: ${err.message}`);
      console.error('Camera error:', err);
    }
  };

  const stopCamera = () => {
    const video = videoRef.current;
    if (video && video.srcObject) {
      const tracks = video.srcObject.getTracks();
      tracks.forEach(track => track.stop());
      video.srcObject = null;
      setIsStreaming(false);
    }

    // Clear canvas
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  };

  // Animation loop for pose detection
  useEffect(() => {
    let animationId;

    const animate = () => {
      if (isStreaming) {
        detectPose(videoRef.current);
        animationId = requestAnimationFrame(animate);
      }
    };

    if (isStreaming) {
      animate();
    }

    return () => {
      if (animationId) {
        cancelAnimationFrame(animationId);
      }
    };
  }, [isStreaming]);

  return (
    <div className="live-camera">
      <h3>Live Camera with Pose Overlay</h3>

      <div className="camera-container">
        <video
          ref={videoRef}
          className="camera-video"
          autoPlay
          playsInline
          muted
        />
        <canvas
          ref={canvasRef}
          className="pose-overlay"
        />
      </div>

      <div className="camera-controls">
        {!isStreaming ? (
          <button onClick={startCamera} className="start-camera-btn">
            üìπ Start Camera
          </button>
        ) : (
          <button onClick={stopCamera} className="stop-camera-btn">
            ‚èπÔ∏è Stop Camera
          </button>
        )}
      </div>

      {error && (
        <div className="camera-error">
          <p>‚ö†Ô∏è {error}</p>
          <p>Make sure to allow camera access when prompted.</p>
        </div>
      )}

      <div className="camera-status">
        <p>Status: {isStreaming ? 'üü¢ Live' : 'üî¥ Offline'}</p>
        <p>Pose Detection: {isStreaming ? 'Active' : 'Inactive'}</p>
      </div>
    </div>
  );
};

export default LiveCamera;
