import React, { useState, useEffect } from 'react';
import ReferenceVideo from './components/ReferenceVideo';
import LiveDance from './components/LiveDance';
import './App.css';

function App() {
  const [isGameStarted, setIsGameStarted] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [resetCountdown, setResetCountdown] = useState(null);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFile(file);
      setIsProcessing(true);
      // Simulate processing
      setTimeout(() => {
        setIsProcessing(false);
      }, 2000);
    }
  };

  const startGame = () => {
    setIsGameStarted(true);
    setIsPaused(false);
  };

  // Countdown effect for reset
  useEffect(() => {
    if (resetCountdown > 0) {
      const timer = setTimeout(() => {
        setResetCountdown(resetCountdown - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (resetCountdown === 0) {
      // Reset the game when countdown reaches 0
      setIsGameStarted(false);
      setIsPaused(false);
      setResetCountdown(null);
    }
  }, [resetCountdown]);

  const resetGame = () => {
    if (resetCountdown === null) {
      setResetCountdown(5);
    }
  };

  return (
    <div className="App">
      <header className="app-header">
        <div className="title-section">
          <span className="title-icon">üéµ</span>
          <h1 className="app-title">Just Dance Clone</h1>
          <span className="title-icon">üíÉ</span>
        </div>
        <p className="app-subtitle">
          Upload a video and dance along with real-time pose detection!
        </p>
      </header>

      <div className="upload-section">
        <input
          type="file"
          accept="video/*"
          onChange={handleFileUpload}
          id="video-upload"
          style={{ display: 'none' }}
        />
        <label htmlFor="video-upload" className="upload-button">
          üìÅ Upload Video
        </label>

        {uploadedFile && (
          <div className="upload-info">
            <p>Selected: {uploadedFile.name}</p>
            {isProcessing && <p>Processing...</p>}
          </div>
        )}
      </div>

      <div className="game-controls">
        <button 
          className="start-button"
          onClick={startGame}
          disabled={!uploadedFile || isProcessing}
        >
          ‚ñ∫ Start Game
        </button>
        <button 
          className="pause-button"
          onClick={pauseGame}
          disabled={!isGameStarted}
        >
          ‚è∏ Pause
        </button>
        <button 
          className="reset-button"
          onClick={resetGame}
        >
          üîÑ Reset
        </button>
      </div>

      <main className="dance-area">
        <div className="video-panels">
          <ReferenceVideo 
            isGameStarted={isGameStarted}
            isPaused={isPaused}
          />
          <LiveDance 
            isGameStarted={isGameStarted}
            isPaused={isPaused}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
