import React, { useState, useEffect, useCallback } from 'react';
import ReferenceVideo from './components/ReferenceVideo';
import LiveDance from './components/LiveDance';
import RealtimeScorer from './components/RealtimeScorer';
import './App.css';

function App() {
  const [isGameStarted, setIsGameStarted] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const [uploadedFile, setUploadedFile] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [gameStartCountdown, setGameStartCountdown] = useState(null);
  const [currentPoseData, setCurrentPoseData] = useState(null);
  const [isGameEnded, setIsGameEnded] = useState(false);
  const [finalScore, setFinalScore] = useState(0);
  const [currentTotalScore, setCurrentTotalScore] = useState(0);

  // Handle pose detection from LiveDance component
  const handlePoseDetected = useCallback((poseData) => {
    setCurrentPoseData(poseData);
  }, []);

  // Handle game ending with final score
  const handleGameEnd = useCallback((score) => {
    console.log('üéÆ Game ended with final score:', score);
    setFinalScore(score);
    setIsGameEnded(true);
    setIsGameStarted(false);
  }, []);

  const handleFileUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setUploadedFile(file);
      setIsProcessing(true);
      // Simulate processing
      setTimeout(() => {
        setIsProcessing(false);
      }, 2000);
    }
  };

  const startGame = () => {
    console.log('üéÆ Start game clicked, current countdown:', gameStartCountdown);
    if (gameStartCountdown === null) {
      console.log('üéÆ Starting countdown...');
      setGameStartCountdown(5);
    }
  };

  // Simple reset function - no countdown needed
  const resetGame = () => {
    console.log('üîÑ Resetting game...');
    setIsGameStarted(false);
    setIsPaused(false);
    setGameStartCountdown(null);
    setIsGameEnded(false);
    setFinalScore(0);
  };

  // Countdown effect for game start
  useEffect(() => {
    if (gameStartCountdown > 0) {
      console.log(`üéÆ Game countdown: ${gameStartCountdown}`);
      const timer = setTimeout(() => {
        setGameStartCountdown(gameStartCountdown - 1);
      }, 1000);
      return () => clearTimeout(timer);
    } else if (gameStartCountdown === 0) {
      console.log('üéÆ Countdown finished - starting game!');
      // Show "GO!" for a moment before starting the game
      setTimeout(() => {
        setIsGameStarted(true);
        setIsPaused(false);
        setGameStartCountdown(null);
      }, 500);
    }
  }, [gameStartCountdown]);


  return (
    <div className="App">
      <header className="app-header">
        <div className="title-section">
          <span className="title-icon">üéµ</span>
          <h1 className="app-title">Just Dance Clone</h1>
          <span className="title-icon">üíÉ</span>
        </div>
        <p className="app-subtitle">
          Upload a video and dance along with real-time pose detection!
        </p>
      </header>

      <div className="upload-section">
        <input
          type="file"
          accept="video/*"
          onChange={handleFileUpload}
          id="video-upload"
          style={{ display: 'none' }}
        />
        <label htmlFor="video-upload" className="upload-button">
          üìÅ Upload Video
        </label>

        {uploadedFile && (
          <div className="upload-info">
            <p>Selected: {uploadedFile.name}</p>
            {isProcessing && <p>Processing...</p>}
          </div>
        )}
      </div>

      <div className="game-controls">
        <button 
          className="start-button"
          onClick={startGame}
          disabled={!uploadedFile || isProcessing || gameStartCountdown !== null}
        >
          {gameStartCountdown !== null ? `üéÆ Starting in ${gameStartCountdown}...` : '‚ñ∫ Start Game'}
        </button>
        <button 
          className="reset-button"
          onClick={resetGame}
        >
          üîÑ Reset
        </button>
      </div>

      <main className="dance-area">
                {gameStartCountdown !== null && (
                  <div className="game-countdown-overlay">
                    <div className="countdown-content">
                      <div className={`countdown-number ${gameStartCountdown === 0 ? 'go-message' : ''}`}>
                        {gameStartCountdown > 0 ? gameStartCountdown : 'GO!'}
                      </div>
                      <div className="countdown-text">
                        {gameStartCountdown > 0 ? 'Get Ready to Dance!' : 'Start Dancing!'}
                      </div>
                    </div>
                  </div>
                )}

                {isGameEnded && (
                  <div className="game-ending-overlay">
                    <div className="ending-content">
                      <div className="ending-title">üéâ GAME OVER! üéâ</div>
                      <div className="final-score-display">
                        <div className="final-score-label">Your Final Score:</div>
                        <div className="final-score-value">{finalScore}</div>
                      </div>
                      <div className="ending-message">
                        {finalScore >= 1000 ? 'üåü AMAZING DANCE! üåü' : 
                         finalScore >= 500 ? 'üíÉ GREAT MOVES! üíÉ' : 
                         finalScore >= 200 ? 'üéµ GOOD EFFORT! üéµ' : 
                         'üí™ KEEP PRACTICING! üí™'}
                      </div>
                    </div>
                  </div>
                )}

        <div className="video-panels">
          <ReferenceVideo 
            isGameStarted={isGameStarted}
            isPaused={isPaused}
            onVideoEnd={() => {
              console.log('üé¨ Video ended, ending game...');
              if (isGameStarted) {
                handleGameEnd(totalScore);
              }
            }}
          />
          <LiveDance 
            isGameStarted={isGameStarted}
            isPaused={isPaused}
            onPoseDetected={handlePoseDetected}
          />
        </div>

                {isGameStarted && (
                  <RealtimeScorer 
                    isGameStarted={isGameStarted}
                    poseData={currentPoseData}
                    onGameEnd={handleGameEnd}
                  />
                )}
      </main>
    </div>
  );
}

export default App;
