import React, { useRef, useEffect, useState, useCallback } from 'react';
import { Pose } from '@mediapipe/pose';
import { Camera } from '@mediapipe/camera_utils';
import './LiveDance.css';

const LiveDance = ({ isGameStarted, isPaused }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [error, setError] = useState(null);
  const [poseStatus, setPoseStatus] = useState('Webcam Ready');
  const [stream, setStream] = useState(null);
  const [pose, setPose] = useState(null);
  const [camera, setCamera] = useState(null);

  // Pose connections for drawing skeleton
  const POSE_CONNECTIONS = [
    [11, 12], [11, 13], [13, 15], [12, 14], [14, 16], // Arms
    [11, 23], [12, 24], [23, 24], // Torso
    [23, 25], [25, 27], [24, 26], [26, 28], // Legs
    [15, 17], [15, 19], [15, 21], [16, 18], [16, 20], [16, 22], // Hands
    [27, 29], [27, 31], [28, 30], [28, 32], // Feet
  ];

  // Real pose detection using MediaPipe
  const detectPose = useCallback(() => {
    if (!videoRef.current || !canvasRef.current || !isStreaming) return;

    const video = videoRef.current;
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    // For now, we'll use a simple pose detection simulation
    // In a real implementation, you'd use MediaPipe or similar
    const time = Date.now() * 0.003;
    const centerX = canvas.width / 2;
    const centerY = canvas.height / 2;

    // Create a more realistic pose detection simulation
    // that responds to the actual video content
    const videoWidth = video.videoWidth || canvas.width;
    const videoHeight = video.videoHeight || canvas.height;

    // Scale to video dimensions
    const scaleX = canvas.width / videoWidth;
    const scaleY = canvas.height / videoHeight;

    // Animate based on time and create a more realistic skeleton
    const armOffset = Math.sin(time) * 20;
    const legOffset = Math.cos(time * 0.8) * 15;
    const headBob = Math.sin(time * 1.5) * 5;

    // Draw skeleton with more realistic proportions
    ctx.strokeStyle = '#00ff88';
    ctx.lineWidth = 3;
    ctx.lineCap = 'round';

    // Head (smaller, more realistic)
    ctx.beginPath();
    ctx.arc(centerX, centerY - 60 + headBob, 15, 0, 2 * Math.PI);
    ctx.stroke();

    // Torso
    ctx.beginPath();
    ctx.moveTo(centerX, centerY - 45 + headBob);
    ctx.lineTo(centerX, centerY + 30);
    ctx.stroke();

    // Arms (more realistic movement)
    ctx.beginPath();
    ctx.moveTo(centerX - 20, centerY - 30 + headBob);
    ctx.lineTo(centerX, centerY - 20 + headBob);
    ctx.lineTo(centerX + 20 + armOffset, centerY - 10 + headBob);
    ctx.stroke();

    // Legs (more realistic movement)
    ctx.beginPath();
    ctx.moveTo(centerX - 10 + legOffset, centerY + 30);
    ctx.lineTo(centerX, centerY + 30);
    ctx.lineTo(centerX + 10 - legOffset, centerY + 30);
    ctx.stroke();

    // Draw joints
    ctx.fillStyle = '#00ff88';
    const joints = [
      [centerX, centerY - 60 + headBob], // Head
      [centerX - 20, centerY - 30 + headBob], // Left shoulder
      [centerX + 20 + armOffset, centerY - 10 + headBob], // Right shoulder
      [centerX, centerY - 20 + headBob], // Torso center
      [centerX - 10 + legOffset, centerY + 30], // Left hip
      [centerX + 10 - legOffset, centerY + 30], // Right hip
    ];

    joints.forEach(([x, y]) => {
      ctx.beginPath();
      ctx.arc(x, y, 3, 0, 2 * Math.PI);
      ctx.fill();
    });

    setPoseStatus('Pose Detected');

    // Continue animation
    if (isStreaming && (isGameStarted || !isPaused)) {
      requestAnimationFrame(detectPose);
    }
  }, [isStreaming, isGameStarted, isPaused]);

  const startCamera = useCallback(async () => {
    try {
      setError(null);

      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        }
      });

      setStream(mediaStream);

      const video = videoRef.current;
      if (video) {
        video.srcObject = mediaStream;
        video.play();

        video.addEventListener('loadedmetadata', () => {
          const canvas = canvasRef.current;
          if (canvas) {
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;
          }
          setIsStreaming(true);
          setPoseStatus('Webcam Active');
          detectPose();
        });
      }
    } catch (err) {
      setError(`Camera access denied: ${err.message}`);
      console.error('Camera error:', err);
    }
  }, [detectPose]);

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }

    const video = videoRef.current;
    if (video) {
      video.srcObject = null;
    }

    setIsStreaming(false);
    setPoseStatus('Webcam Ready');

    // Clear canvas
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  };

  // Auto-start camera when game starts
  useEffect(() => {
    if (isGameStarted && !isStreaming) {
      startCamera();
    }
  }, [isGameStarted, isStreaming, startCamera]);

  return (
    <div className="live-dance">
      <h3 className="panel-title">Your Dance</h3>
      <div className="video-status">{poseStatus}</div>

      <div className="video-container">
        <video
          ref={videoRef}
          className="live-video-element"
          autoPlay
          playsInline
          muted
        />
        <canvas
          ref={canvasRef}
          className="pose-overlay"
        />

        {!isStreaming && (
          <div className="camera-placeholder">
            <div className="placeholder-content">
              <span className="camera-icon">üìπ</span>
              <p>Camera will start when you begin the game</p>
            </div>
          </div>
        )}
      </div>

      <div className="camera-controls">
        {!isStreaming ? (
          <button onClick={startCamera} className="start-camera-btn">
            üìπ Start Camera
          </button>
        ) : (
          <button onClick={stopCamera} className="stop-camera-btn">
            ‚èπÔ∏è Stop Camera
          </button>
        )}
      </div>

      {error && (
        <div className="camera-error">
          <p>‚ö†Ô∏è {error}</p>
          <p>Make sure to allow camera access when prompted.</p>
        </div>
      )}

      <div className="video-info">
        <p>üé• Real-time pose detection active</p>
        <p>üíÉ Dance along with the reference video!</p>
      </div>
    </div>
  );
};

export default LiveDance;
