import React, { useRef, useEffect, useState, useCallback } from 'react';
import { Pose } from '@mediapipe/pose';
import { Camera } from '@mediapipe/camera_utils';
import './LiveDance.css';

const LiveDance = ({ isGameStarted, isPaused }) => {
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const [isStreaming, setIsStreaming] = useState(false);
  const [error, setError] = useState(null);
  const [poseStatus, setPoseStatus] = useState('Webcam Ready');
  const [stream, setStream] = useState(null);
  const [pose, setPose] = useState(null);
  const [camera, setCamera] = useState(null);

  // Pose connections for drawing skeleton
  const POSE_CONNECTIONS = [
    [11, 12], [11, 13], [13, 15], [12, 14], [14, 16], // Arms
    [11, 23], [12, 24], [23, 24], // Torso
    [23, 25], [25, 27], [24, 26], [26, 28], // Legs
    [15, 17], [15, 19], [15, 21], [16, 18], [16, 20], [16, 22], // Hands
    [27, 29], [27, 31], [28, 30], [28, 32], // Feet
  ];

  // Real MediaPipe pose detection
  const detectPose = useCallback((results) => {
    if (!canvasRef.current || !results.poseLandmarks) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Clear canvas
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const landmarks = results.poseLandmarks;

    // Draw connections
    ctx.strokeStyle = '#00ff88';
    ctx.lineWidth = 3;
    ctx.lineCap = 'round';

    POSE_CONNECTIONS.forEach(([startIdx, endIdx]) => {
      const start = landmarks[startIdx];
      const end = landmarks[endIdx];

      if (start && end && start.visibility > 0.5 && end.visibility > 0.5) {
        ctx.beginPath();
        ctx.moveTo(start.x * canvas.width, start.y * canvas.height);
        ctx.lineTo(end.x * canvas.width, end.y * canvas.height);
        ctx.stroke();
      }
    });

    // Draw landmarks
    ctx.fillStyle = '#00ff88';
    landmarks.forEach((landmark) => {
      if (landmark.visibility > 0.5) {
        ctx.beginPath();
        ctx.arc(landmark.x * canvas.width, landmark.y * canvas.height, 4, 0, 2 * Math.PI);
        ctx.fill();
      }
    });

    setPoseStatus('Pose Detected');
  }, [POSE_CONNECTIONS]);

  const startCamera = useCallback(async () => {
    try {
      setError(null);

      const mediaStream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 640 },
          height: { ideal: 480 },
          facingMode: 'user'
        }
      });

      setStream(mediaStream);

      const video = videoRef.current;
      if (video) {
        video.srcObject = mediaStream;
        video.play();

        video.addEventListener('loadedmetadata', () => {
          const canvas = canvasRef.current;
          if (canvas) {
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;
          }
          setIsStreaming(true);
          setPoseStatus('Webcam Active');
          detectPose();
        });
      }
    } catch (err) {
      setError(`Camera access denied: ${err.message}`);
      console.error('Camera error:', err);
    }
  }, [detectPose]);

  const stopCamera = () => {
    if (stream) {
      stream.getTracks().forEach(track => track.stop());
      setStream(null);
    }

    const video = videoRef.current;
    if (video) {
      video.srcObject = null;
    }

    setIsStreaming(false);
    setPoseStatus('Webcam Ready');

    // Clear canvas
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      ctx.clearRect(0, 0, canvas.width, canvas.height);
    }
  };

  // Auto-start camera when game starts
  useEffect(() => {
    if (isGameStarted && !isStreaming) {
      startCamera();
    }
  }, [isGameStarted, isStreaming, startCamera]);

  return (
    <div className="live-dance">
      <h3 className="panel-title">Your Dance</h3>
      <div className="video-status">{poseStatus}</div>

      <div className="video-container">
        <video
          ref={videoRef}
          className="live-video-element"
          autoPlay
          playsInline
          muted
        />
        <canvas
          ref={canvasRef}
          className="pose-overlay"
        />

        {!isStreaming && (
          <div className="camera-placeholder">
            <div className="placeholder-content">
              <span className="camera-icon">üìπ</span>
              <p>Camera will start when you begin the game</p>
            </div>
          </div>
        )}
      </div>

      <div className="camera-controls">
        {!isStreaming ? (
          <button onClick={startCamera} className="start-camera-btn">
            üìπ Start Camera
          </button>
        ) : (
          <button onClick={stopCamera} className="stop-camera-btn">
            ‚èπÔ∏è Stop Camera
          </button>
        )}
      </div>

      {error && (
        <div className="camera-error">
          <p>‚ö†Ô∏è {error}</p>
          <p>Make sure to allow camera access when prompted.</p>
        </div>
      )}

      <div className="video-info">
        <p>üé• Real-time pose detection active</p>
        <p>üíÉ Dance along with the reference video!</p>
      </div>
    </div>
  );
};

export default LiveDance;
