import React, { useState, useEffect } from 'react';
import VideoPlayer from './components/VideoPlayer';
import AngleCharts from './components/AngleCharts';
import PoseDataViewer from './components/PoseDataViewer';
import PoseComparison from './components/PoseComparison';
import './App.css';

function App() {
  const [poseData, setPoseData] = useState(null);
  const [angleData, setAngleData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const loadPoseData = async () => {
    setLoading(true);
    setError(null);

    try {
      // Load JSONL pose data
      const response = await fetch('http://localhost:5007/api/poses');
      if (!response.ok) {
        throw new Error('Failed to load pose data');
      }
      const data = await response.json();
      setPoseData(data);

      // Load CSV angle data
      const angleResponse = await fetch('http://localhost:5007/api/angles');
      if (!angleResponse.ok) {
        throw new Error('Failed to load angle data');
      }
      const angleData = await angleResponse.json();
      setAngleData(angleData);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadPoseData();
  }, []);

  return (
    <div className="App">
      <header className="App-header">
        <h1>MediaPipe Pose Tracking Viewer</h1>
        <p>Visualize pose landmarks and joint angles from your dance video</p>
      </header>

      <main className="App-main">
        {loading && (
          <div className="loading">
            <p>Loading pose data...</p>
          </div>
        )}

        {error && (
          <div className="error">
            <p>Error: {error}</p>
            <button onClick={loadPoseData}>Retry</button>
          </div>
        )}

        {poseData && angleData && (
          <>
            <VideoPlayer 
              poseData={poseData}
              videoSrc="http://localhost:5007/api/video"
            />

            <PoseComparison referencePoses={poseData} />

            <div className="data-section">
              <AngleCharts angleData={angleData} />
              <PoseDataViewer poseData={poseData} />
            </div>
          </>
        )}

        {!poseData && !loading && !error && (
          <div className="no-data">
            <p>No pose data found. Make sure you've run the pose extraction first.</p>
            <button onClick={loadPoseData}>Load Data</button>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
