import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './RealtimeScorer.css';

const RealtimeScorer = ({ isGameStarted, poseData, onGameEnd, onScoreUpdate }) => {
  const [totalScore, setTotalScore] = useState(0);
  const [currentPoints, setCurrentPoints] = useState(0);
  const [pointGain, setPointGain] = useState(null);
  const [showPointGain, setShowPointGain] = useState(false);
  const [accuracy, setAccuracy] = useState(0);
  // Removed unused frameCount and gameStartTime variables

  // Send pose data to backend for scoring every 0.5 seconds
  const sendPoseForScoring = useCallback(async (poseData) => {
    if (!isGameStarted || !poseData) {
      console.log('âš ï¸ Not sending pose - game not started or no pose data');
      return;
    }

    // Double-check that game is still active before sending
    if (!isGameStarted) {
      console.log('ðŸ›‘ Game ended, stopping pose scoring');
      return;
    }

    console.log(`ðŸ“Š Sending pose for scoring: ${poseData.kp.length} landmarks`);
    console.log('ðŸ“Š Pose data sample:', poseData.kp.slice(0, 3)); // Show first 3 landmarks

    try {
      const response = await axios.post('http://localhost:5000/api/compare-pose', {
        live_pose: poseData
      });

      const { points_earned, similarity, message } = response.data;
      console.log(`ðŸŽ¯ Received: ${points_earned} points (${similarity.toFixed(1)}% similarity) - ${message}`);

      // Update scores
      setCurrentPoints(points_earned);
      const newTotalScore = totalScore + points_earned;
      setTotalScore(newTotalScore);

      // Notify parent of score update
      if (onScoreUpdate) {
        onScoreUpdate(newTotalScore);
      }

      // Update accuracy with the actual similarity percentage from backend
      setAccuracy(Math.round(similarity));

      // Show point gain animation
      if (points_earned > 0) {
        setPointGain(points_earned);
        setShowPointGain(true);

        // Hide point gain after animation
        setTimeout(() => {
          setShowPointGain(false);
        }, 1000);
      }

      console.log(`ðŸŽ¯ Score: ${points_earned} points (${similarity.toFixed(1)}% similarity) - ${message}`);

    } catch (error) {
      console.error('âŒ Error scoring pose:', error);
    }
  }, [isGameStarted, totalScore, onScoreUpdate]);

  // Process pose data with proper interval (every 0.5 seconds)
  useEffect(() => {
    if (poseData && isGameStarted) {
      console.log('ðŸŽ¯ Starting pose scoring interval with pose data:', poseData.kp.length, 'landmarks');

      // Send pose for scoring every 0.5 seconds for more responsive scoring
      const interval = setInterval(() => {
        // Check if game is still active before sending
        if (isGameStarted) {
          console.log('ðŸŽ¯ Interval triggered - sending pose for scoring');
          sendPoseForScoring(poseData);
        } else {
          console.log('ðŸ›‘ Game ended, stopping interval');
          clearInterval(interval);
        }
      }, 500); // 0.5 seconds

      return () => {
        console.log('ðŸŽ¯ Clearing pose scoring interval');
        clearInterval(interval);
      };
    } else {
      console.log('âš ï¸ Not starting pose scoring - missing poseData or game not started');
    }
  }, [poseData, isGameStarted, sendPoseForScoring]);

  // Reset scores when game resets
  useEffect(() => {
    if (!isGameStarted) {
      console.log('ðŸ›‘ Game ended - resetting all scores and stopping scoring');
      setTotalScore(0);
      setCurrentPoints(0);
      setPointGain(null);
      setShowPointGain(false);
      setAccuracy(0);
    } else {
      // Set game start time when game begins
      setGameStartTime(Date.now());
    }
  }, [isGameStarted]);

  // Game timer removed - game should end when video ends, not after fixed time
  // The video ending is handled by the ReferenceVideo component calling onVideoEnd

  return (
    <div className="realtime-scorer">
      <div className="score-display">
        <div className="total-score">
          <div className="score-label">Total Score</div>
          <div className="score-value">{totalScore}</div>
        </div>

        <div className="current-score">
          <div className="score-label">Current</div>
          <div className="score-value">{currentPoints}</div>
        </div>

        <div className="accuracy">
          <div className="score-label">Accuracy</div>
          <div className="score-value">{accuracy}%</div>
        </div>
      </div>

      {/* Point Gain Animation */}
      {showPointGain && pointGain > 0 && (
        <div className="point-gain-animation">
          <div className="point-gain-text">
            +{pointGain}
          </div>
        </div>
      )}

      <div className="scoring-info">
        <p>ðŸŽ¯ Real-time pose scoring active</p>
        <p>ðŸ’ƒ Follow the reference video closely!</p>
      </div>
    </div>
  );
};

export default RealtimeScorer;
