import React, { useState, useEffect, useRef } from 'react';
import './PoseComparison.css';

const PoseComparison = ({ referencePoses }) => {
  const [isTracking, setIsTracking] = useState(false);
  const [currentScore, setCurrentScore] = useState(0);
  const [totalScore, setTotalScore] = useState(0);
  const [sessionScores, setSessionScores] = useState([]);
  const [feedback, setFeedback] = useState('');
  const [similarity, setSimilarity] = useState(0);
  const [sessionStats, setSessionStats] = useState({
    totalFrames: 0,
    averageSimilarity: 0,
    bestScore: 0,
    worstScore: 0
  });

  const scoreHistoryRef = useRef([]);
  const frameCountRef = useRef(0);

  // Compute joint angles from MediaPipe landmarks
  const computeJointAngles = (landmarks) => {
    if (!landmarks || landmarks.length < 33) {
      return { elbow_L: 0, elbow_R: 0, knee_L: 0, knee_R: 0 };
    }

    // MediaPipe pose landmark indices
    const L_SHOULDER = 11, L_ELBOW = 13, L_WRIST = 15;
    const R_SHOULDER = 12, R_ELBOW = 14, R_WRIST = 16;
    const L_HIP = 23, L_KNEE = 25, L_ANKLE = 27;
    const R_HIP = 24, R_KNEE = 26, R_ANKLE = 28;

    const computeAngle = (p1_idx, p2_idx, p3_idx) => {
      const p1 = landmarks[p1_idx].slice(0, 3); // x, y, z
      const p2 = landmarks[p2_idx].slice(0, 3);
      const p3 = landmarks[p3_idx].slice(0, 3);

      const v1 = [p1[0] - p2[0], p1[1] - p2[1], p1[2] - p2[2]];
      const v2 = [p3[0] - p2[0], p3[1] - p2[1], p3[2] - p2[2]];

      const dot = v1[0] * v2[0] + v1[1] * v2[1] + v1[2] * v2[2];
      const norm1 = Math.sqrt(v1[0] * v1[0] + v1[1] * v1[1] + v1[2] * v1[2]);
      const norm2 = Math.sqrt(v2[0] * v2[0] + v2[1] * v2[1] + v2[2] * v2[2]);

      if (norm1 === 0 || norm2 === 0) return 0;

      const cosAngle = Math.max(-1, Math.min(1, dot / (norm1 * norm2)));
      return Math.acos(cosAngle);
    };

    return {
      elbow_L: computeAngle(L_SHOULDER, L_ELBOW, L_WRIST),
      elbow_R: computeAngle(R_SHOULDER, R_ELBOW, R_WRIST),
      knee_L: computeAngle(L_HIP, L_KNEE, L_ANKLE),
      knee_R: computeAngle(R_HIP, R_KNEE, R_ANKLE)
    };
  };

  // Compute cosine similarity between two angle vectors
  const computeCosineSimilarity = (angles1, angles2) => {
    const joints = ['elbow_L', 'elbow_R', 'knee_L', 'knee_R'];
    const vec1 = joints.map(joint => angles1[joint] || 0);
    const vec2 = joints.map(joint => angles2[joint] || 0);

    const dot = vec1.reduce((sum, val, i) => sum + val * vec2[i], 0);
    const norm1 = Math.sqrt(vec1.reduce((sum, val) => sum + val * val, 0));
    const norm2 = Math.sqrt(vec2.reduce((sum, val) => sum + val * val, 0));

    if (norm1 === 0 || norm2 === 0) return 0;

    const cosSim = dot / (norm1 * norm2);
    return Math.max(0, Math.min(1, (cosSim + 1) / 2)); // Map to [0, 1]
  };

  // Calculate score based on similarity
  const calculateScore = (similarity) => {
    // Scoring system: 
    // 0.9+ = 100 points (excellent)
    // 0.8-0.9 = 80-99 points (very good)
    // 0.7-0.8 = 60-79 points (good)
    // 0.6-0.7 = 40-59 points (fair)
    // 0.5-0.6 = 20-39 points (poor)
    // <0.5 = 0-19 points (very poor)

    if (similarity >= 0.9) return 100;
    if (similarity >= 0.8) return Math.round(80 + (similarity - 0.8) * 200);
    if (similarity >= 0.7) return Math.round(60 + (similarity - 0.7) * 200);
    if (similarity >= 0.6) return Math.round(40 + (similarity - 0.6) * 200);
    if (similarity >= 0.5) return Math.round(20 + (similarity - 0.5) * 200);
    return Math.round(similarity * 40);
  };

  // Get feedback based on similarity
  const getFeedback = (similarity) => {
    if (similarity >= 0.9) return "Perfect! Excellent form! üéâ";
    if (similarity >= 0.8) return "Great job! Very close to reference! üëè";
    if (similarity >= 0.7) return "Good! Keep practicing to improve! üí™";
    if (similarity >= 0.6) return "Not bad! Focus on matching the reference more closely.";
    if (similarity >= 0.5) return "Keep trying! Watch the reference and adjust your pose.";
    return "Keep practicing! You're getting there! üéØ";
  };

  // Process pose comparison
  const processPoseComparison = (userLandmarks) => {
    if (!referencePoses || referencePoses.length === 0) return;

    // Get current reference pose (use first one for now, could be time-synced)
    const refPose = referencePoses[0];
    if (!refPose || !refPose.kp) return;

    // Compute angles for both poses
    const userAngles = computeJointAngles(userLandmarks);
    const refAngles = computeJointAngles(refPose.kp);

    // Calculate similarity
    const currentSimilarity = computeCosineSimilarity(userAngles, refAngles);
    setSimilarity(currentSimilarity);

    // Calculate score
    const frameScore = calculateScore(currentSimilarity);
    setCurrentScore(frameScore);

    // Update session scores
    scoreHistoryRef.current.push(frameScore);
    frameCountRef.current++;

    // Update session statistics
    const scores = scoreHistoryRef.current;
    const totalScore = scores.reduce((sum, score) => sum + score, 0);
    const averageScore = totalScore / scores.length;
    const bestScore = Math.max(...scores);
    const worstScore = Math.min(...scores);

    setTotalScore(totalScore);
    setSessionStats({
      totalFrames: frameCountRef.current,
      averageSimilarity: currentSimilarity,
      bestScore: bestScore,
      worstScore: worstScore,
      averageScore: averageScore
    });

    // Update feedback
    setFeedback(getFeedback(currentSimilarity));
  };

  // Start tracking
  const startTracking = () => {
    setIsTracking(true);
    setCurrentScore(0);
    setTotalScore(0);
    setSessionScores([]);
    scoreHistoryRef.current = [];
    frameCountRef.current = 0;
    setFeedback('Start moving to see your score!');
  };

  // Stop tracking
  const stopTracking = () => {
    setIsTracking(false);
    const finalScore = scoreHistoryRef.current.length > 0 
      ? Math.round(scoreHistoryRef.current.reduce((sum, score) => sum + score, 0) / scoreHistoryRef.current.length)
      : 0;

    setSessionScores(prev => [...prev, {
      timestamp: new Date().toISOString(),
      finalScore: finalScore,
      frameCount: frameCountRef.current,
      averageSimilarity: sessionStats.averageSimilarity
    }]);
  };

  // Reset session
  const resetSession = () => {
    setIsTracking(false);
    setCurrentScore(0);
    setTotalScore(0);
    setSessionScores([]);
    setSimilarity(0);
    setFeedback('');
    scoreHistoryRef.current = [];
    frameCountRef.current = 0;
    setSessionStats({
      totalFrames: 0,
      averageSimilarity: 0,
      bestScore: 0,
      worstScore: 0
    });
  };

  // Simulate pose data for demo (replace with real camera input)
  useEffect(() => {
    if (!isTracking) return;

    const interval = setInterval(() => {
      // Simulate user landmarks (replace with real MediaPipe data)
      const simulatedLandmarks = Array.from({length: 33}, () => [
        Math.random() * 2 - 1,  // x
        Math.random() * 2 - 1,  // y
        Math.random() * 2 - 1,  // z
        Math.random()           // visibility
      ]);

      processPoseComparison(simulatedLandmarks);
    }, 100); // 10fps

    return () => clearInterval(interval);
  }, [isTracking, referencePoses]);

  return (
    <div className="pose-comparison">
      <div className="comparison-header">
        <h2>üéØ Pose Comparison & Scoring</h2>
        <p>Compare your movements against the reference video and earn points!</p>
      </div>

      <div className="scoring-dashboard">
        <div className="score-cards">
          <div className="score-card current">
            <div className="score-value">{currentScore}</div>
            <div className="score-label">Current Score</div>
          </div>

          <div className="score-card total">
            <div className="score-value">{Math.round(totalScore / Math.max(frameCountRef.current, 1))}</div>
            <div className="score-label">Average Score</div>
          </div>

          <div className="score-card similarity">
            <div className="score-value">{(similarity * 100).toFixed(1)}%</div>
            <div className="score-label">Similarity</div>
          </div>
        </div>

        <div className="feedback-section">
          <div className="feedback-text">{feedback}</div>
        </div>

        <div className="controls">
          <button 
            className={`control-btn ${isTracking ? 'stop' : 'start'}`}
            onClick={isTracking ? stopTracking : startTracking}
          >
            {isTracking ? '‚èπÔ∏è Stop Tracking' : '‚ñ∂Ô∏è Start Tracking'}
          </button>

          <button 
            className="control-btn reset"
            onClick={resetSession}
            disabled={isTracking}
          >
            üîÑ Reset Session
          </button>
        </div>
      </div>

      <div className="session-stats">
        <h3>Session Statistics</h3>
        <div className="stats-grid">
          <div className="stat-item">
            <span className="stat-label">Frames Tracked:</span>
            <span className="stat-value">{sessionStats.totalFrames}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Best Score:</span>
            <span className="stat-value">{sessionStats.bestScore}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Worst Score:</span>
            <span className="stat-value">{sessionStats.worstScore}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Average Score:</span>
            <span className="stat-value">{sessionStats.averageScore.toFixed(1)}</span>
          </div>
        </div>
      </div>

      {sessionScores.length > 0 && (
        <div className="session-history">
          <h3>Session History</h3>
          <div className="history-list">
            {sessionScores.map((session, index) => (
              <div key={index} className="history-item">
                <div className="history-time">
                  {new Date(session.timestamp).toLocaleTimeString()}
                </div>
                <div className="history-score">
                  Final Score: {session.finalScore}
                </div>
                <div className="history-frames">
                  Frames: {session.frameCount}
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default PoseComparison;
