import { MentionProvider, StateBasedMentionProviderConfig } from './AgentContextTypes.js';
import 'react';
import 'zod';

/**
 * Hook to create and register a state-based mention provider
 * This is an all-in-one hook that handles provider creation and registration
 */
declare function useStateBasedMentionProvider<T>(config: StateBasedMentionProviderConfig<T>): void;
/**
 * Hook to get all registered mention providers
 */
declare function useMentionProviders(): MentionProvider[];
/**
 * Hook to get mention providers by trigger
 */
declare function useMentionProvidersByTrigger(trigger: string): MentionProvider[];

export { useMentionProviders, useMentionProvidersByTrigger, useStateBasedMentionProvider };
